pub mod day_1_2020 {
    pub fn get_floor_number_from_code(code: &str) -> i32 {
        let mut floor = 0;
        for c in code.chars(){
            match c {
                '(' => floor += 1,
                ')' => floor -= 1,
                _ => panic!("Invalid Char: {}, found", c)
            }
        }
        return floor
    }

    pub fn check_first_basement_position(code: &str) -> i32 {
        let mut floor = 0;
        let mut first_basement_position:i32 = 0;
        for (i, c) in code.chars().enumerate() {
            match c {
                '(' => floor += 1,
                ')' => {
                    floor -= 1;
                    if floor < 0 {
                        first_basement_position = (i + 1) as i32;
                        break
                    }
                }
                _ => panic!("Invalid Char: {}, found", c)
            }
        }
        return first_basement_position;
    }
}

